import React from 'react';
import styled from 'styled-components'
import Produto from './Components/Produto';
import Carrinho from './Components/Carrinho';
import Header from './Components/Header';
import Filtros from './Components/Filtros';
import './App.css';


const Page = styled.div`
  display: flex;
  flex-direction: column;
  .center {
    position: relative;
    .lista-produtos {
      display: flex;
      justify-content: space-evenly;
      flex-wrap: wrap;
      margin: 0 auto;
    }
    #carrinho {
      opacity: 0;
    }
    .lista-carrinho {
      -webkit-animation: slide-in-top 0.5s cubic-bezier(0.250, 0.460, 0.450, 0.940) both;
	    animation: slide-in-top 0.5s cubic-bezier(0.250, 0.460, 0.450, 0.940) both;
      width: 350px;
      box-shadow: 0 2px 5px #ccc;
      border-radius: 0 0 15px 15px;
      background-color: #fff;
      padding: 10px;
      position: absolute;
      top: 0;
      right: 0;
      h2 {
        text-align: center;
      }
      p.carrinho-vazio {
        margin: 20px;
        text-align: center;
        color: orange;
      }
    }
  }
  @media screen and (max-width: 530px) {
    .center {
      .lista-carrinho {
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        width: 100vw;
        height: calc(100vh - 150px)
      }
    }
  }
`

export default class App extends React.Component {
  state= {
    produtos: [
      {
        id: 1,
	      name: "Camisa1",
	      value: 40,
        quantidade: 1,
	      imageUrl: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTTHXxrOv_smp8CG9aDP9v2AMWJUeoSe5NlyhU3iLOxRWwSZvwugGZB1OObVe-VQcChS4g&usqp=CAU"
      },
      {
        id: 2,
	      name: "Camisa2",
	      value: 32,
        quantidade: 1,
	      imageUrl: "data:image/jpeg;base64,"
      },
      {
        id: 3,
	      name: "Camisa3",
	      value: 25,
        quantidade: 1,
	      imageUrl: "data:image/jpeg;base64,"
      },
      {
        id: 4,
	      name: "Camisa4",
	      value: 87,
        quantidade: 1,
	      imageUrl: "data:image/jpeg;base64,"
      },
      {
        id: 5,
	      name: "Camisa5",
	      value: 18,
        quantidade: 1,
	      imageUrl: "data:image/jpeg;base64,"
      },
      {
        id: 6,
	      name: "Camisa6",
	      value: 37,
        quantidade: 1,
        carrinho: false,
	      imageUrl: "data:image/jpeg;base64,"
      },
      {
        id: 7,
	      name: "Camisa7",
	      value: 48,
        quantidade: 1,
	      imageUrl: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQsRQrWHv0I_8iK8Z6yRoH9XeluVezPUHCu8A&usqp=CAU"
      },
      {
        id: 8,
	      name: "Camisa8",
	      value: 90,
        quantidade: 1,
	      imageUrl: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTyAPFUU5ruuNtpC146nacNQlR2d8Q1r-olfQ&usqp=CAU"
      },
      {
        id: 9,
	      name: "Camisa9",
	      value: 61,
        quantidade: 1,
        carrinho: false,
	      imageUrl: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ6wgmtWngKp1rIb03DAJkNlJfo_LnnYxw2gQ&usqp=CAU"
      }
    ],
    carrinho: [],
    valorTotalCarrinho: 0,
    busca: ''.toLocaleLowerCase()
  }

  AdicionaCarrinho = (produto) => { 
    if (!this.state.carrinho.length) {
      const novoProduto = [...this.state.carrinho, produto]
      const valorCarrinho = this.state.valorTotalCarrinho + produto.value
      this.setState({carrinho: novoProduto, valorTotalCarrinho: valorCarrinho})
    } else {
      const novo = this.state.carrinho.map((item) => {
        if(item.id === produto.id) {
          item.quantidade = item.quantidade + 1
          this.setState({valorTotalCarrinho: this.state.valorTotalCarrinho + produto.value})
        }
        return item
      })
      const filtro = novo.filter((valor) => {
        return valor.id === produto.id
      })
      console.log(filtro)
      if (!filtro.length) {
        novo.push(produto)
        this.setState({valorTotalCarrinho: this.state.valorTotalCarrinho + produto.value})
      }
    
      this.setState({carrinho: novo})

    }
    
  }

  RemoverCarrinho = (produto) => {
    if(produto.quantidade > 1) {
      const todosItens = this.state.carrinho.map((item) => {
        if(produto.id === item.id) {
          item.quantidade = item.quantidade - 1
          this.setState({valorTotalCarrinho: this.state.valorTotalCarrinho - produto.value})
        }
        return item
      })
      this.setState({carrinho: todosItens})
    } else {
      const itensFiltrados = this.state.carrinho.filter((item) => {
        return produto.id !== item.id
      })
      this.setState({carrinho: itensFiltrados, valorTotalCarrinho: this.state.valorTotalCarrinho - produto.value})
    }
  }

  AbrirCarrinho = () => {
    const carrinho = document.getElementById('carrinho')
    carrinho.classList.toggle('lista-carrinho')
  }

  BuscaProdutos = (event) => {
    this.setState({busca: event.target.value})
  }
/*-------------------------------------------------*/
  render() {
    const produtosFiltrados = this.state.produtos.filter((produto) => {
      return produto.name.toLowerCase().includes(this.state.busca)
    })

    const listaProdutos = produtosFiltrados.map((produto) => {
      return <Produto 
        key={produto.id}
        caminhoImage={produto.imageUrl}
        nomeProduto={produto.name}
        preco={produto.value}
        onclick={() => this.AdicionaCarrinho(produto)}
      />
    })

    const listaCarrinho = this.state.carrinho.map((item) => {
      return <Carrinho 
        key={item.id}
        qntProduto={item.quantidade}
        nomeProduto={item.name}
        botaoRemover={() => this.RemoverCarrinho(item)}
    />
    })

    const quantidadeProduto = this.state.produtos.length

    return (
      <Page>
        <Header 
          quntidadeItensCarrinho={this.state.carrinho.length} 
          botaoAbrirCarrinho={this.AbrirCarrinho} 
          inputBusca={this.BuscaProdutos}
          valor={this.state.busca}
        />

        <div className='center filtro'>
          <Filtros />
          <p>Quantidade de produtos: {quantidadeProduto}</p>
          <div className='lista-produtos'>
              {listaProdutos}
          </div>
          <div id='carrinho'>
            <h2>Carrinho</h2>
            {this.state.carrinho.length ? '' : <p className='carrinho-vazio'>Carrinho Vazio</p>}
            <div>{listaCarrinho}</div>
            {this.state.carrinho.length ? <p>Valor total: R$ {this.state.valorTotalCarrinho.toFixed(2)}</p> : '' }
          </div>
        </div>
      </Page>
    )
  }
}